            );
        }
    }


    return (
        <div className="TodoList">
            <h4>Todo List üçö</h4>
            <input className="searchbar" value={search} onChange={onChangeSearch} placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
            <div className="list_wrapper">
      {/* todoItemÏùÑ 3Î≤à Î∞òÎ≥µÌï¥ÏÑú Ï∂úÎ†• */}
      {/* {id, isDone, content, creatDate}  */}
      {/* {id, isDone, content, creatDate} -> propsÎ°ú TodoItem Ïª¥Ìè¨ÎÑåÌä∏Ïóê Ï†ÑÎã¨  */}
                {getSearchResult().map((it) => (
                    <TodoItem key={it.id} {...it} onUpdate={onUpdate} onDelete={onDelete} />                   
                ))}
            </div>
        </div>
    );
}

export default TodoList;
